# Utility functions for cryptographic operations

def message_to_number(message):
    # Convert message to numeric representation using ASCII hex
    hex_representation = message.encode('ascii').hex()
    return int(hex_representation, 16)


def number_to_message(number):
    # Convert number back to message
    hex_str = hex(number)[2:]  # remove '0x' prefix
    if len(hex_str) % 2:
        hex_str = '0' + hex_str
    return bytes.fromhex(hex_str).decode('ascii')


